import { openai } from '@ai-sdk/openai';
import { streamText } from 'ai';

async function getAiStream() {
  const { textStream } = await streamText({
    model: openai('gpt-4o'),
    prompt: 'Tell me a short story about a friendly robot.',
  });
  return textStream;
}

// server/api/ai/stream-chat.post.ts
import { defineEventHandler, setResponseHeader, sendStream } from 'h3';
import { openai } from '@ai-sdk/openai';
import { streamText } from 'ai';

export default defineEventHandler(async (event) => {
  const body = await readBody(event);
  const userPrompt = body.prompt || 'Tell me a joke.';
  const { textStream: aiTextStream } = await streamText({
    model: openai(process.env.OPENAI_API_KEY ? 'gpt-4o' : 'gpt-3.5-turbo'),
    prompt: userPrompt,
  });
  setResponseHeader(event, 'Content-Type', 'text/plain; charset=utf-8');
  setResponseHeader(event, 'Cache-Control', 'no-cache');
  setResponseHeader(event, 'Transfer-Encoding', 'chunked');
  return sendStream(event, aiTextStream);
});

